(493, 'Arceus', 'Arceus', 'Arceus', 'Normal', '', 'Alpha', '0.500', '0.500', 120, 70, 1000000, 5120, '1.0', '5.0', 10, 20, 30, 40, 50, 60, 'Dieu', 'Blanc', 'UB', '35'),
(400, 'H\'rz', 'rth', 'rteh', 'Acier', '', 'rj', '0.500', '0.500', 120, 70, 1000000, 5120, '1.0', '5.0', 0, 0, 0, 0, 0, 0, 'fj', 'Blanc', 'NU', '0'),
